<script setup>
import Button from '../elements/Button.vue'
import StepTop from '../elements/StepTop.vue'
import PayDialog from '../elements/PayDialog.vue'

import { ref, onMounted } from 'vue'
import { storeToRefs } from 'pinia'
import { useMainStore } from '../../stores/main'

const store = useMainStore()
const { currentStep, appData } = storeToRefs(store)

const discount = ref(52)
const oldPrice = ref(60)
const price = ref(29)
const activateDiscount = ref(false)
const showDialog = ref(false)
const showPayment = ref(false)

function skip() {
  if (!activateDiscount.value) {
    showDialog.value = true
    discount.value = 68
    price.value = 19
    activateDiscount.value = true
  } else {
    currentStep.value++
  }
}

const count = 8
const started = ref(false)
const timerData = ref({
  min: 0,
  sec: 0,
})

function startTimer() {
  if (started.value) {
    return
  }
  var start_time = new Date()
  var stop_time = start_time.setMinutes(start_time.getMinutes() + count)

  var countdown = setInterval(function () {
    var now = new Date().getTime()
    var remain = stop_time - now
    var min = Math.floor((remain % (1000 * 60 * 60)) / (1000 * 60))
    var sec = Math.floor((remain % (1000 * 60)) / 1000)
    min = min < 10 ? '0' + min : `${min}`
    sec = sec < 10 ? '0' + sec : `${sec}`
    timerData.value.min = min
    timerData.value.sec = sec

    if (remain <= 0) {
      clearInterval(countdown)
    }
  }, 1000)
  started.value = true
}

onMounted(() => {
  startTimer()
})
</script>

